import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
const Note = (props) => (
    <div className="card mt-4">
    <div className="card-body">
      <h4 className="card-title category">{props.note.category}</h4>
      <div className="card-text mb-2 description">{props.note.description}</div>
      <Link className="btn btn-info"to={"/edit/" + props.note._id}>edit</Link>
      <button className="btn btn-danger" onClick={() => { props.deleteNote(props.note._id) }}>
        Delete
      </button>
    </div>
  </div>
);

export default class NotesList extends Component {
  constructor(props) {
    super(props);

    this.deleteNote = this.deleteNote.bind(this);

    this.state = { notes: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/notes/allNotes")
      .then((response) => {
        this.setState({ notes: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  deleteNote(id) {
    axios.delete("http://localhost:5000/notes/" + id).then((response) => {
      console.log(response.data);
    });

    this.setState({
      notes: this.state.notes.filter((el) => el._id !== id),
    });
  }

  noteList() {
    return this.state.notes.map((currentnote) => {
      return (
        <Note
          note={currentnote}
          deleteNote={this.deleteNote}
          key={currentnote._id}
        />
      );
    });
  }

  filterer(){
    let filterInput = document.getElementById("filterInput");
    filterInput.addEventListener("keyup", <this.filterNames/>);
  }

   filterNames() {
    //Get value of input
    let filterValue = document
      .getElementById("filterInput")
      .value.toUpperCase();
    console.log(filterValue);
    // get ul
    let ul = document.getElementById("names");
    // get li from ul
    let li = ul.querySelectorAll("li.collection-item");
    //loop thorugh collection item lis
    for (let i = 0; i < li.length; i++) {
      let a = li[i].getElementsByTagName("a")[0];
      if (a.innerHTML.toUpperCase().indexOf(filterValue) > -1) {
        li[i].style.display = " ";
      } else {
        li[i].style.display = "none";
      }
    }
  }

  render() {
    return (
      <div>
        <h3>All Notes</h3>
        <input type="text" className="form-control-lg rounded" placeholder="Search for Category.." />
        <ul className="names">
          <ol className="notes">{this.noteList()}</ol>
        </ul>
    </div>
    );
  }
}
