import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import EditNote from "./edit-note";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

const Note = (props) => (
  <tr>
    <td>{props.note.category}</td>
    <td>{props.note.description}</td>
    <td>
      <Router>
        <Routes>
          <Route path="/allNotes/edit/props.note._id" element={<EditNote />} />
        </Routes>
      </Router>
      |
      <a
        href="/"
        onClick={() => {
          props.deleteExercise(props.note._id);
        }}
      >
        delete
      </a>
    </td>
  </tr>
);

export default class NotesList extends Component {
  constructor(props) {
    super(props);

    this.deleteNote = this.deleteNote.bind(this);

    this.state = { notes: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/notes/allNotes")
      .then((response) => {
        this.setState({ notes: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  deleteNote(id) {
    axios
      .delete("http://localhost:5000/notes/allNotes" + id)
      .then((response) => {
        console.log(response.data);
      });

    this.setState({
      notes: this.state.notes.filter((el) => el._id !== id),
    });
  }

  noteList() {
    return this.state.notes.map((currentnote) => {
      return (
        <Note
          note={currentnote}
          deleteNote={this.deleteNote}
          key={currentnote._id}
        />
      );
    });
  }

  render() {
    return (
      <div>
        <h3>Logged Notes</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Category</th>
              <th>Description</th>
            </tr>
          </thead>
          <tbody>{this.noteList()}</tbody>
        </table>
      </div>
    );
  }
}
