import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
const Note = (props) => (
  <tr>
    <td>{props.note.category}</td>
    <td>{props.note.description}</td>
    <td>
      <Link to={"/edit/" + props.note._id}>edit</Link>|
      <a
        href="/"
        onClick={() => {
          props.deleteNote(props.note._id);
        }}
      >
        delete
      </a>
    </td>
  </tr>
);

export default class NotesList extends Component {
  constructor(props) {
    super(props);

    this.deleteNote = this.deleteNote.bind(this);

    this.state = { notes: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/notes/allNotes")
      .then((response) => {
        this.setState({ notes: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  deleteNote(id) {
    axios.delete("http://localhost:5000/notes/" + id).then((response) => {
      console.log(response.data);
    });

    this.setState({
      notes: this.state.notes.filter((el) => el._id !== id),
    });
  }

  noteList() {
    return this.state.notes.map((currentnote) => {
      return (
        <Note
          note={currentnote}
          deleteNote={this.deleteNote}
          key={currentnote._id}
        />
      );
    });
  }

  render() {
    return (
      <div>
        <h3>All Notes</h3>
        {/* <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Category</th>
              <th>Description</th>
            </tr>
          </thead>
          <tbody>{this.noteList()}</tbody>
        </table> */}
        Notes.for
        Notes.array.forEach((Note) => {
          <div className="card mt-4">
          <div className="card-body">
            <h4 class="card-title"></h4>
            <div class="card-text mb-2">{this.noteList()}</div>
          </div>
        </div>
        });
    </div>
    );
  }
}
