import React, { useState,useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
const Note = (props) => (
    <div className="card mt-4">
    <div className="card-body">
      <h4 className="card-title">{props.note.category}</h4>
      <div className="card-text mb-2">{props.note.description}</div>
      <Link className="btn btn-info"to={"/edit/" + props.note._id}>edit</Link>
      <button className="btn btn-danger" onClick={()=>{ props.deleteNote(props.note._id) }}>
        Delete
      </button>
    </div>
  </div>
);
const NotesList = () => {
    const [notes,setNotes] = useState({notes:[]});

    useEffect(()=>{
     axios
      .get("http://localhost:5000/notes/allNotes")
      .then((response) => {
        // this.setState({ notes: response.data });
        setNotes({ notes: response.data })
      })
      .catch((error) => {
        console.log(error);
      });
    })

    const deleteNote = id =>{
        axios.delete("http://localhost:5000/notes/" + id).then((response) => {
          console.log(response.data);
        });
    
        this.setState({
          notes: this.state.notes.filter((el) => el._id !== id),
        });
      }

     const noteList = () => {
        return this.state.notes.map((currentnote) => {
          return (
            <Note
              note={currentnote}
              deleteNote={this.deleteNote}
              key={currentnote._id}
            />
          );
        });
      }
      

  return (
    <div>
        <h3>All Notes</h3>
        <div>{noteList}</div>
    </div>
  )
}

export default NotesList
